INSERT INTO `nomi_voice` (`voice_id`,`cid`,`type`,`proid`,`title`,`cover`,`cover_detail`,`content`,`play_num`,`share_num`,`info`,`is_check`,`status`,`like_num`,`collect_num`,`create_time`,`update_time`) VALUES ('18','2','1','0','致创业者：男人的狼性你有多少','/uploads/voice/20190819/Donald/5d5a6a0aa96ae.png','/uploads/voice/20190819/Donalddetail/5d5a6a0aab6cb.png','<p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:微软雅黑;color:rgb(51,51,51);font-size:14px"><span style="font-family:微软雅黑"><img src="https://www.nomiyy.com/uploads/article/20190819/330635dfc80420e42c8aaba663a41d83.png" title="https://www.nomiyy.com/uploads/article/330635dfc80420e42c8aaba663a41d83.png" alt="https://www.nomiyy.com/uploads/article/330635dfc80420e42c8aaba663a41d83.png"/></span></span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:微软雅黑;color:rgb(51,51,51);font-size:14px"><span style="font-family:微软雅黑">是不是觉得很久没有在头条看到如此</span>“毫不掩饰”“振奋人心”“激情满满”的文（ji）章（tang）了呢？当你们阅读的时候，是希望从中获得鼓励，还是更希望获得实用信息？创业路漫漫，每个人身上都有一根弦紧绷，压力之下，适当的鼓励其实也很重要。希望疲惫一天的你看完这篇，能储蓄满满能量，迎接充满希望的新一天！</span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:微软雅黑;color:rgb(51,51,51);font-size:14px"><span style="font-family:微软雅黑">如果你喜欢实用可学习的内容，请再戳二、三篇！</span></span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;padding:0 0 0 0 ;text-align:right;line-height:25px"><span style=";font-family:Calibri;color:rgb(51,51,51);font-size:14px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:16px">1</span><strong><span style="font-family: 微软雅黑;color: rgb(192, 0, 0);font-size: 16px">男人可以虚怀若谷，可以低调行事，可以沉默寡言，但绝不可没有自信。</span></strong></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:center;line-height:26px;background:rgb(255,255,255)"><img width="32" height="32" src="https://www.nomiyy.com/static/admin/function/ueditor/themes/default/images/spacer.gif"/><span style="font-family: 微软雅黑;color: rgb(62, 62, 62);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p style="margin-top:21px;margin-right:0;margin-bottom:21px;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:24px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(62, 62, 62);letter-spacing: 1px;font-size: 17px"><span style="font-family:微软雅黑">自信，是撑起男人的骨骼。没有自信，就没有男人味。没有自信，这男人就废了。男人的自信是强大的，它使男人能勇敢面对一切；男人的自信又是脆弱的，它经不住女人的轻轻一击。是女人，当懂得保护男人的自信。</span></span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:16px">2</span><strong><span style="font-family: 微软雅黑;color: rgb(192, 0, 0);font-size: 16px">男人可以不辉煌，可以不成功，但绝不可没有事业心。</span></strong></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p style="margin-top:21px;margin-right:0;margin-bottom:21px;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:24px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(62, 62, 62);letter-spacing: 1px;font-size: 17px"><span style="font-family:微软雅黑">为兴趣也好，为理想也好，为生计也好，男人一生必须在做着至少一份事业，至少也是一件事情。碌碌无为可能是天意，游手好闲绝不允许。</span></span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:16px">3</span><strong><span style="font-family: 微软雅黑;color: rgb(192, 0, 0);font-size: 16px">男人可以智商不高，可以不睿智博学，可以不机智敏捷，但男人必须真实。</span></strong></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p style="margin-top:21px;margin-right:0;margin-bottom:21px;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:24px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(62, 62, 62);letter-spacing: 1px;font-size: 17px"><span style="font-family:微软雅黑">夸夸其谈、自吹自耀、雕虫小技、卖弄聪明，这些是男人的毛病而不是男人应有的作派。真实的男人，玩笑归玩笑，调侃归调侃，但必然面对现实！惟其直面现实，承诺必现实可信，一言九鼎；守信必尽心尽力，不负众望。真实男人，视阴招损招为不齿。男人有谋也是阳谋，绝不是阴谋。真实是要有勇气的，男人的勇气不在于舍生取义，不在于笑对生死，而在于面对！回避不像男人，逃避绝不是男人！</span></span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:center;line-height:26px;background:rgb(255,255,255)"><img src="https://www.nomiyy.com/uploads/article/20190819/fd7d25e1e03c040d477430a6369c67aa.png" title="https://www.nomiyy.com/uploads/article/fd7d25e1e03c040d477430a6369c67aa.png" alt="https://www.nomiyy.com/uploads/article/fd7d25e1e03c040d477430a6369c67aa.png"/><img width="32" height="32" src="https://www.nomiyy.com/static/admin/function/ueditor/themes/default/images/spacer.gif"/><span style="font-family: 微软雅黑;color: rgb(62, 62, 62);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:16px">4</span><strong><span style="font-family: 微软雅黑;color: rgb(192, 0, 0);font-size: 16px">男人可以闲适懒散，可以不拘小节，可以不修边幅，但男人必须有肩膀，必须担起责任。</span></strong></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p style="margin-top:21px;margin-right:0;margin-bottom:21px;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:24px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(62, 62, 62);letter-spacing: 1px;font-size: 17px"><span style="font-family:微软雅黑">男人的肩膀天生就是承重的，它不但是女人靠着时的一份心理安全，更应担起属于它的实际责任。男人，没有理由在责任面前肩膀下滑。该吃亏就吃亏，该磨难就磨难，该牺牲只能挺而牺牲。挺直，就是男人的风范！</span></span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:16px">5</span><strong><span style="font-family: 微软雅黑;color: rgb(192, 0, 0);font-size: 16px">男人可以和气，可以温和，可以一让再让，但男人必须有自己坚守的基本原则。</span></strong></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p style="margin-top:21px;margin-right:0;margin-bottom:21px;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:24px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(62, 62, 62);letter-spacing: 1px;font-size: 17px"><span style="font-family:微软雅黑">没有任何原则的男人，是没有骨气的男人，是没有骨头的男人，是该硬时硬不起的软弱男人。持什么样的基本原则，造就什么样的男人。而原则过多的男人，必是小处着眼、斤斤计较，那就走向了男人的反面。</span></span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:16px">6</span><strong><span style="font-family: 微软雅黑;color: rgb(192, 0, 0);font-size: 16px">男人可以没有气势，但必须有气度。</span></strong></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p style="margin-top:21px;margin-right:0;margin-bottom:21px;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:24px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(62, 62, 62);letter-spacing: 1px;font-size: 17px"><span style="font-family:微软雅黑">男人的胸围可以小于女人，胸怀必须大于女人。男人肚子可以盛不下婴儿，但必须盛下女人。普通男人的肚里就应能撑船，宰相的肚里应该能开飞机。男人大不大，不是看他个头高不高，而是看他的胸怀宽不宽。有原则有气量的男人，其行事风格往往是先礼而后兵。礼，可以是听过算过，可以是设身处地想想人家的难处，可以是主动沟通，可以是不计较这一回的损失和受伤。礼的次数与形式可以依事情的性质而定。兵，可以是严正警告，可以是以其人之道还治其人之身，甚至可以是此仇不报誓不为人。但就日常生活而已，兵的最高境界是删除你，是永不理你！</span></span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:16px">7</span><strong><span style="font-family: 微软雅黑;color: rgb(192, 0, 0);font-size: 16px">男人就是坚强，男人就是坚韧。</span></strong></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p style="margin-top:21px;margin-right:0;margin-bottom:21px;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:24px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(62, 62, 62);letter-spacing: 1px;font-size: 17px"><span style="font-family:微软雅黑">男人天生就有承受失败的韧性。是男人，只会在挫折中奋起，只会愈挫愈奋，决不会被挫折打垮。男人爱上女人，矢志不渝是男人，穷追猛打是男人，非她不娶是男人，退而织网是男人，放纵一次不轻弹的眼泪也是男人。但真真检验和显示情场大男人的，是男人的放手！是男人，放手时把痛苦默默咽下，放手时不埋怨迁怒对方，放手时心甘情愿承受所有损失，放手时唯一的愿望就是希望她幸福。她幸福，我快乐，是为真男人。</span></span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:16px">8</span><strong><span style="font-family: 微软雅黑;color: rgb(192, 0, 0);font-size: 16px">男人的爱是大爱。</span></strong></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:27px;background:rgb(255,255,255)"><span style="font-family: 微软雅黑;color: rgb(51, 51, 51);letter-spacing: 1px;font-size: 17px">&nbsp;</span></p><p style="margin-top:21px;margin-right:0;margin-bottom:21px;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:24px;backgr